import React, { useState } from 'react';
import './styles/Home.css';
import DP from './icons/logobk.png';

const Auth = () => {
  const [isSignUp, setIsSignUp] = useState(false);
  const [isLogin, setIsLogin] = useState(false);

  const handleChange = (e) => {
    setIsSignUp(e);
  };

  const handleLogin = (e) => {
    setIsLogin(e);
  };

  return (
    <>
      {isLogin && (
        <div>
          <div className="container">
            <div className="wrapper-ac">
              <img src={DP} height="80px" width="80px" className="dp-img" />
            </div>
          </div>
        </div>
      )}
      {!isLogin && (
        <div>
          {!isSignUp && (
            <div className="container">
              <div className="wrapper-ac">
                <h1>Login</h1>
                <input type="text" placeholder="@username" />
                <input type="password" placeholder="password" />
                <div className="btn">
                  <button
                    className="btn-sign-in"
                    onClick={() => handleLogin(true)}
                  >
                    Login
                  </button>
                  <button
                    className="btn-sign-up"
                    onClick={() => handleChange(true)}
                  >
                    Sign-Up
                  </button>
                </div>

                <p>
                  <input type="checkbox" name="agree" value="yes" />I accept the
                  Terms of Service and Privacy Policy
                </p>
              </div>
            </div>
          )}
          {isSignUp && (
            <div className="container">
              <div className="wrapper-ac">
                <h1>Sign-Up</h1>
                <input type="text" placeholder="Enter your name" />
                <input type="text" placeholder="Enter your email" />
                <input type="password" placeholder="Enter password" />
                {/* <input type="password" placeholder="re-enter password" /> */}
                <div className="btn">
                  <button className="btn-sign-in">Sign-Up</button>
                  <button
                    className="btn-sign-up"
                    onClick={() => handleChange(false)}
                  >
                    Login
                  </button>
                </div>

                <p>
                  <input type="checkbox" name="agree" value="yes" />I accept the
                  Terms of Service and Privacy Policy
                </p>
              </div>
            </div>
          )}
        </div>
      )}
    </>
  );
};

export default Auth;
