import React, { useEffect, useState } from 'react';
import s from './styles/Auth.module.css';
import DP from './icons/logobk.png';
import Account from './Account';
import { color, style } from '@mui/system';
import axios from 'axios';

const Auth = () => {
  const [isSignUp, setIsSignUp] = useState(false);
  const [isLogin, setIsLogin] = useState(false);

  const handleChange = (e) => {
    setIsSignUp(e);
  };

  const handleLogin = (e) => {
    setIsLogin(e);
  };

  const [value, setValue] = useState({
    name: '',
    email: '',
    password: '',
    repassword: ''
  });

  // const handleCh = (e) => {
  //   const { name, value } = e.target;

  //   setValue((data) => {
  //     return {
  //       ...data,
  //       [name]: value
  //     };
  //   });
  // };

  const register = (e) => {
    e.preventDefault();

    axios.post('/register', value).then(
      (res) => {
        console.log(res);
      },
      [value]
    );
  };
  return (
    <>
      {isLogin && <Account />}
      {!isLogin && (
        <div>
          {!isSignUp && (
            <div className={s.container}>
              <div className={s.wrapper}>
                <h1>Login</h1>
                <form>
                  <input
                    name="username"
                    value={setValue.value}
                    type="text"
                    placeholder="@username"
                    onChange={handleCh}
                  />
                  <input
                    name="password"
                    value={setValue.value}
                    type="password"
                    placeholder="password"
                    onChange={handleCh}
                  />
                </form>
                <div className={s.btn}>
                  <button
                    className={s.btn_sign_in}
                    onClick={() => handleLogin(true)}
                  >
                    Login
                  </button>
                  <button
                    className={s.btn_sign_up}
                    onClick={() => handleChange(true)}
                  >
                    Sign-Up
                  </button>
                </div>

                <p>
                  Please login to your account. If not click Sign Up to create
                  one.
                </p>
              </div>
            </div>
          )}
          {isSignUp && (
            <div className={s.container}>
              <div className={s.wrapper}>
                <h1>Sign-Up</h1>
                <form>
                  <input
                    name="name"
                    value={setValue.value}
                    type="text"
                    placeholder="Enter your name"
                    onChange={handleCh}
                  />
                  <input
                    name="email"
                    value={setValue.value}
                    type="text"
                    placeholder="Enter your email"
                    onChange={handleCh}
                  />
                  <input
                    name="password"
                    value={setValue.value}
                    type="password"
                    placeholder="Enter password"
                    onChange={handleCh}
                  />
                  <input
                    name="repassword"
                    value={setValue.value}
                    type="password"
                    placeholder="re-enter password"
                    onChange={handleCh}
                  />
                </form>
                <div className={s.btn}>
                  <button className={s.btn_sign_in} onClick={register}>
                    Sign-Up
                  </button>
                  <button
                    className={s.btn_sign_up}
                    onClick={() => handleChange(false)}
                  >
                    Login
                  </button>
                </div>
                <p>
                  <input
                    type="checkbox"
                    name="agree"
                    value="checked"
                    style={{ width: '15px', margin: '0px 10px 10px 0px' }}
                  />
                  I accept the Terms of Service and Privacy Policy
                </p>
              </div>
            </div>
          )}
        </div>
      )}
    </>
  );
};

export default Auth;
