import React, { useState } from 'react';
import s from './styles/Auth.module.css';
import DP from './icons/logobk.png';
import Account from './Account';
import { color, style } from '@mui/system';

const Auth = () => {
  const [isSignUp, setIsSignUp] = useState(false);
  const [isLogin, setIsLogin] = useState(false);

  const handleChange = (e) => {
    setIsSignUp(e);
  };

  const handleLogin = (e) => {
    setIsLogin(e);
  };

  const [value, setValue] = useState({
    name: '',
    value: ''
  });

  return (
    <>
      {isLogin && <Account />}
      {!isLogin && (
        <div>
          {!isSignUp && (
            <div className={s.container}>
              <div className={s.wrapper}>
                <h1>Login</h1>
                <form>
                  <input
                    name="username"
                    value={setValue.value}
                    type="text"
                    placeholder="@username"
                  />
                  <input
                    name="password"
                    value={setValue.value}
                    type="password"
                    placeholder="password"
                  />
                </form>
                <div className={s.btn}>
                  <button
                    className={s.btn_sign_in}
                    onClick={() => handleLogin(true)}
                  >
                    Login
                  </button>
                  <button
                    className={s.btn_sign_up}
                    onClick={() => handleChange(true)}
                  >
                    Sign-Up
                  </button>
                </div>

                <p>
                  Please login to your account. If not click Sign Up to create
                  one.
                </p>
              </div>
            </div>
          )}
          {isSignUp && (
            <div className={s.container}>
              <div className={s.wrapper}>
                <h1>Sign-Up</h1>
                <form>
                  <input
                    name="name"
                    value={setValue.value}
                    type="text"
                    placeholder="Enter your name"
                  />
                  <input
                    name="email"
                    value={setValue.value}
                    type="text"
                    placeholder="Enter your email"
                  />
                  <input
                    name="password"
                    value={setValue.value}
                    type="password"
                    placeholder="Enter password"
                  />
                  <input
                    name="repassword"
                    value={setValue.value}
                    type="password"
                    placeholder="re-enter password"
                  />
                </form>
                <div className={s.btn}>
                  <button className={s.btn_sign_in}>Sign-Up</button>
                  <button
                    className={s.btn_sign_up}
                    onClick={() => handleChange(false)}
                  >
                    Login
                  </button>
                </div>
                <p>
                  <input
                    type="checkbox"
                    name="agree"
                    value="checked"
                    style={{ width: '15px', margin: '0px 10px 10px 0px' }}
                  />
                  I accept the Terms of Service and Privacy Policy
                </p>
              </div>
            </div>
          )}
        </div>
      )}
    </>
  );
};

export default Auth;
